import React from 'react';
import './App.css';
import Login from './login/Login';
import Map from './map/Map';
import Profile from './profile/Profile';
import Signup from './signup/Signup';
import Header from './shared/Header';

const PAGES = [
    {
        id: 'login',
        name: 'Логин',
        component: () => <Login />
    },
    {
        id: 'map',
        name: 'Карта',
        component: () => <Map />
    },
    {
        id: 'profile',
        name: 'Профиль',
        component: () => <Profile />
    },
    {
        id: 'signup',
        name: 'Регистрация',
        component: () => <Signup />
    }
];

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {activePage: 'login'};
    }

    changePage = (page) => {
        this.setState({activePage: page});
    };

    render() {
        return (
            <div className="App">
                <Header pages={PAGES} changePage={this.changePage} activePage={this.state.activePage} />
                {PAGES[this.state.activePage]}
            </div>
        );
    }
}

export default App;
